!(function () {
	var t,
		e = document.getElementById(darkSwitchId);
	if (e) {
		(t =
			null !== localStorage.getItem(darkSwitchId) &&
			"dark" === localStorage.getItem(darkSwitchId)),
			(e.checked = t)
				? document.body.setAttribute("data-theme", "dark")
				: document.body.removeAttribute("data-theme"),
			e.addEventListener("change", function (t) {
				e.checked
					? (document.body.setAttribute("data-theme", "dark"),
					  localStorage.setItem(darkSwitchId, "dark"))
					: (document.body.removeAttribute("data-theme"),
					  localStorage.removeItem(darkSwitchId));
			});
	}
})();
