!(function () {
	var t,
		e = document.getElementById(darkSwitchKey);
	if (e) {
		(t =
			null !== localStorage.getItem(darkSwitchKey) &&
			"dark" === localStorage.getItem(darkSwitchKey)),
			(e.checked = t)
				? document.body.setAttribute("data-theme", "dark")
				: document.body.removeAttribute("data-theme"),
			e.addEventListener("change", function (t) {
				e.checked
					? (document.body.setAttribute("data-theme", "dark"),
						localStorage.setItem(darkSwitchKey, "dark"))
					: (document.body.removeAttribute("data-theme"),
						localStorage.removeItem(darkSwitchKey));
			});
	}
})();
